public class Config : Configuration<Config>
{
    int myInteger;
    string myString;

    public string MyString
    {
        get { return myString; }
        set { myString = value; }
    }
    
    public int MyInteger
    {
        get { return myInteger; }
        set { myInteger = value; }
    }
}
